public function getDiscountCategoryId($categoryid) {
		$query = $this->db->query("SELECT * FROM " . DB_PREFIX . "category_discount WHERE category_id = '" . $categoryid . "'");
        if($query->row)
            return $query->row;
        else
            return '';
	}

