				public function productshippingcharge($product_id) {
	$query = $this->db->query("SELECT shipping_charge FROM " . DB_PREFIX . "product_shipping_charge WHERE product_id = '" . (int)$product_id . "'");

	//return $query->row['shipping_charge'];
	if($query->row){
	return $query->row['shipping_charge'];
	} else {
	return false;
	}
}

